import React from 'react'
import { Line, Pie, Bar } from "react-chartjs-2";

import {
  Chart as ChartJS,
  LineElement,
  BarElement,
  ArcElement,
  CategoryScale,
  LinearScale,
  PointElement,
  Legend,
  Tooltip
} from "chart.js";

ChartJS.register(
  LineElement,
  BarElement,
  ArcElement,
  CategoryScale,
  LinearScale,
  PointElement,
  Legend,
  Tooltip
);

export default function analytics() {
  return (
      <div className="space-y-10">

      {/* KPI CARDS */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        
        <div className="bg-white p-6 rounded-xl shadow text-center">
          <h2 className="text-lg font-semibold text-gray-600">Total Users</h2>
          <p className="text-3xl font-bold mt-2">120</p>
        </div>

        <div className="bg-white p-6 rounded-xl shadow text-center">
          <h2 className="text-lg font-semibold text-gray-600">Total Scans</h2>
          <p className="text-3xl font-bold mt-2">340</p>
        </div>

        <div className="bg-white p-6 rounded-xl shadow text-center">
          <h2 className="text-lg font-semibold text-gray-600">Images Uploaded</h2>
          <p className="text-3xl font-bold mt-2">256</p>
        </div>

      </div>

      {/* CHART GRID */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">

        {/* LINE CHART */}
        <div className="bg-white p-6 rounded-xl shadow max-w-sm mx-auto w-full">
          <h3 className="text-xl font-bold mb-4 text-gray-700 text-center">Daily Scan Activity</h3>
          <Line
            height={180}
            data={{
              labels: ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"],
              datasets: [
                {
                  label: "Scans",
                  data: [12, 19, 7, 14, 22, 30, 25],
                  borderWidth: 2,
                },
              ],
            }}
            options={{
              maintainAspectRatio: false
            }}
          />
        </div>

        {/* PIE CHART */}
        <div className="bg-white p-6 rounded-xl shadow max-w-sm mx-auto w-full">
          <h3 className="text-xl font-bold mb-4 text-gray-700 text-center">Skin Type Distribution</h3>
          <Pie
            height={180}
            data={{
              labels: ["Oily", "Dry", "Combination", "Normal"],
              datasets: [
                {
                  data: [40, 20, 25, 15],
                },
              ],
            }}
            options={{
              maintainAspectRatio: false
            }}
          />
        </div>

        {/* BAR CHART (optional) */}
        <div className="bg-white p-6 rounded-xl shadow max-w-sm mx-auto w-full">
          <h3 className="text-xl font-bold mb-4 text-gray-700 text-center">Common Skin Issues</h3>
          <Bar
            height={180}
            data={{
              labels: ["Acne", "Redness", "Dryness", "Dark Spots"],
              datasets: [
                {
                  label: "Cases",
                  data: [55, 32, 48, 27],
                  borderWidth: 1,
                },
              ],
            }}
            options={{
              maintainAspectRatio: false
            }}
          />
        </div>

      </div>
    </div>
  )
}
